select A.student_name  as member_A,  B.student_name  as member_B, C.student_name  as member_C from SchoolA A, SchoolB B, SchoolC C where A.student_name  != B.student_name  && A.student_name  != C.student_name  && B.student_name  != C.student_name  && A.student_id != B.student_id && B.student_id != C.student_id && A.student_id != C.student_id;